import React, {Component} from 'react';
import './App.css';
// import Product from './components/Product';

class App extends Component {
  constructor(props) {
    super(props); 
    this.state = {
      products: [],
      counters: []
    }
  }

  componentDidMount() {
    fetch('http://localhost:3001/api/getList')
    .then(response => response.json())
    .then(data => {
      this.setState({products: data})
      data.map(d => {
        console.log(d.name);
        let a = this.state.counters.slice(); //creates the clone of the state
        a[d.name] = 0;
        this.setState({arr: a});
      });
    })
    .catch(error => {
      console.log('error')
    })
  }

  addCount = () =>Â {
    this.setState({counter: this.state.counter + 1})
  }

  subCount = () => {
      if(this.state.counter >= 1) {
          this.setState({counter: this.state.counter - 1})
      }
  }

  render() {
    return (
      <div className="App">
        <div className="container">
          <div className="row margin-div">
            <div className="col-md-8">
              <div className="row">
              {
                this.state.products.map((product, i) => {
                  return <div className="col-md-4" key={i}>
                    <div className="card card-opt">
                        <div className="card-body">
                          <h5 className="card-title">{product.name}</h5>
                          <p className="card-text">{product.discount}</p>
                          <div align="center">
                              <p>{this.state.counters[product.name]}</p>
                              <button className="btn btn-danger btn-add" onClick={this.subCount}> - </button>
                              <button className="btn btn-primary btn-add" onClick={this.addCount}> + </button>
                          </div>
                        </div>
                    </div>
                  </div>
                })
              }
              </div>
              <div className="row margin-div">
                <div className="offset-md-9 col-md-3">
                  <button className="btn btn-success btn-block">Generar Orden</button>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="row order">
                <h2>Orden</h2>
                <table className="table">
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    ); 
  }
}

export default App;
