import React, {Component} from 'react';

class Product extends Component {
    state = {
        counter: 0
    }

    addCount = () =>Â {
        this.setState({counter: this.state.counter + 1})
    }

    subCount = () => {
        if(this.state.counter >= 1) {
            this.setState({counter: this.state.counter - 1})
        }
    }

    render() {
        return (
          <div className="App">
            <div className="card card-opt">
                <div className="card-body">
                <h5 className="card-title">{this.props.data.name}</h5>
                <p className="card-text">{this.props.data.discount}</p>
                <div align="center">
                    <p>{this.state.counter}</p>
                    <button className="btn btn-danger btn-add" onClick={this.subCount}> - </button>
                    <button className="btn btn-primary btn-add" onClick={this.addCount}> + </button>
                </div>
                </div>
            </div>
          </div>
        ); 
      }
}

export default Product;
