import React, {Component} from 'react';
import './App.css';
import Product from './components/Product';

class App extends Component {
  constructor(props) {
    super(props); 
    this.state = {
      products: [],
      counters: {}
    }
  }

  componentDidMount() {
    fetch('http://localhost:3001/api/getList')
    .then(response => response.json())
    .then(data => {
      this.setState({products: data})
      this.setState({counters: {'PANTS': 0, 'TSHIRT': 0, 'HAT': 0}})
      console.log(data)

    })
    .catch(error => {
      console.log('error')
    })
  }

  render() {
    return (
      <div className="App">
        <div className="container">
          <div className="row margin-div">
            <div className="col-md-8">
              <div className="row">
              {
                this.state.products.map((product, i) => {
                  return <div className="col-md-4" key={i}>
                    <Product data={product} counter={this.state.counters[product.name]}/>
                  </div>
                })
              }
              </div>
              <div className="row margin-div">
                <div className="offset-md-9 col-md-3">
                  <button className="btn btn-success btn-block">Generar Orden</button>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="row order">
                <h2>Orden</h2>
                <table className="table">
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    ); 
  }
}

export default App;
