import React, {Component} from 'react';
import './App.css';
// import Product from './components/Product';

class App extends Component {
  constructor(props) {
    super(props); 
    this.state = {
      products: [],
      counters: {'PANTS': 6, 'TSHIRT': 0, 'HAT': 0}
    }
  }

  componentDidMount() {
    fetch('http://localhost:3001/api/getList')
    .then(response => response.json())
    .then(data => {
      this.setState({products: data})
      console.log(data)
    })
    .catch(error => {
      console.log('error')
    })
  }

  increment = (name, e) => {
    // this.setState({count: this.state.count + 1})
    var x = this.state.counters[name] + 1;
    this.setState(prevState => ({
      counters: {
          ...prevState.counters,
          [name]: x,
      },
  }));
  }

  decrement = () => {
    // this.setState({count: this.state.count - 1})
  }

  render() {
    return (
      <div className="App">
        <div className="container">
          <div className="row margin-div">
            <div className="col-md-8">
              <div className="row">
              {
                this.state.products.map((product, i) => {
                  return <div className="col-md-4" key={i}>
                  <div className="card card-opt">
                      <div className="card-body">
                      <h5 className="card-title">{product.name}</h5>
                      <p className="card-text">{product.discount}</p>
                      <div align="center">
                          <p>{this.state.counters[product.name]}</p>
                          <button className="btn btn-danger btn-add" onClick={this.increment.bind(this, product.name)}> - </button>
                          <button className="btn btn-primary btn-add" onClick={this.increment.bind(this, product.name)}> + </button>
                      </div>
                      </div>
                  </div>
                  </div>
                })
              }
              </div>
              <div className="row margin-div">
                <div className="offset-md-9 col-md-3">
                  <button className="btn btn-success btn-block">Generar Orden</button>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="row order">
                <h2>Orden</h2>
                <table className="table">
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    ); 
  }
}

export default App;
