import React, {Component} from 'react';
import './App.css';

class App extends Component {
  state = {
    products: [],
    pz: {PANTS: 0, TSHIRT: 0, HAT: 0}
  }

  componentDidMount() {
    fetch('http://localhost:3001/api/getList')
    .then(response => response.json())
    .then(data => {
      this.setState({products: data})
      console.log(data)
    })
    .catch(error => {
      console.log('error')
    })
  }

  sumPiece = (e) => {
    console.log(e);
  }

  render() {
    return (
      <div className="App">
        <div className="container">
          <div className="row margin-div">
            <div className="col-md-8">
              <div className="row">
              {
                this.state.products.map((product, i) => {
                  return <div className="col-md-4" key={i}>
                    <div className="card card-opt">
                        <div className="card-body">
                          <h5 className="card-title">{product.name}</h5>
                          <p className="card-text">{product.discount}</p>
                          <div align="center">
                            <input defaultValue={this.state.pz[product.name]} type="text" className="form-control input-quantity" />
                            <button className="btn btn-primary btn-add" onClick={this.sumPiece}>Add</button>
                          </div>
                        </div>
                    </div>
                  </div>
                })
              }
              </div>
            </div>
            <div className="col-md-4">

            </div>
          </div>
        </div>
      </div>
    ); 
  }
}

export default App;
